import jxl.*
import com.eviware.soapui.model.testsuite.TestRunner.Status


def filepath = "C:/Users/YogitaJunghare/Downloads/SDET/API/ST.xls"
File Excel = new File(filepath)

// i want to fetch the workbook and the sheet which contain data 
Workbook wb = Workbook.getWorkbook(Excel)

// want to fetch the sheet 
Sheet sh = wb.getSheet("Sheet1")

//fetch no of rows and no of columns

def rowsize = sh.getRows().toInteger();
def columnsize = sh.getColumns().toInteger();

log.info "$rowsize and $columnsize"

//fetch value in the variable

for (def i=1;i<rowsize;i++)
{
   for(def j=0	;j<columnsize;j++)
   {
   	if(j==0)
   	{
   	     email = sh.getCell(j,i).getContents()
   		log.info "$email"
   	}
   	else
   	{
   		firstname = sh.getCell(j,i).getContents()
   		log.info "$firstname"
   		lastname = sh.getCell(j,i).getContents()
   		log.info "$lastname"
   	}
//   	else
//   	{
//   		lastname = sh.getCell(j,i).getContents()
//   		log.info "$lastname"
//   	}
   }	
   // get TestCase
def tc = testRunner.testCase.testSuite.project.testSuites["DDT"].testCases["tc"]

tc.testSteps["token"].setPropertyValue("email",email)
tc.testSteps["token"].setPropertyValue("FirstName",firstname)
tc.testSteps["token"].setPropertyValue("LastName",lastname)

//run test synchronously 
def runner = tc.run(null, false)

// show the test case result status
log.info "Status: $runner.status, time taken for TestCase was: $runner.timeTaken ms"
log.info "Number of test steps which ran: " + runner.results.size()

// assert that it didn't fail
assert runner.status != Status.FAILED : runner.reason

// assert that only four steps ran!!
//assert runner.results.size() == 4


}

import com.eviware.soapui.support.XmlHolder

def response = context.testCase.getTestStepByName('DBConnection').getPropertyValue("responseAsXml")
log.info "$response"

def count = context.expand('${DBConnection#ResponseAsXml#count(//ResultSet/Row/CUSTOMERS.EMAIL)}').toInteger()
log.info count

for (def i=1;i<=count;i++)
{
  email = context.expand('${DBConnection#ResponseAsXml#//ResultSet/Row['+i+']/CUSTOMERS.EMAIL}') 
  firstname = context.expand('${DBConnection#ResponseAsXml#//ResultSet/Row['+i+']/CUSTOMERS.FIRSTNAME}') 
  lastname = context.expand('${DBConnection#ResponseAsXml#//ResultSet/Row['+i+']/CUSTOMERS.LASTNAME}') 

  log.info"email: $email"
  log.info"firstname: $firstname"	
  log.info"lastname: $lastname"	

}

